const fruits = [
  'Apple',
  'Banana',
  'Blueberry',
  'Grapes',
  'Lemon',
  'Mango',
  'Kiwi',
  'Orange',
  'Peach',
  'Pear',
  'Pineapple',
  'Plum',
  'Raspberry',
  'Strawberry',
  'Watermelon',
];

class ClearableMultiSelectExample extends StatelessWidget {
  @override
  Widget build(BuildContext _) => FMultiSelect<String>.rich(
    hint: const Text('Select a fruit'),
    format: Text.new,
    clearable: true,
    children: [
      for (final fruit in fruits) .item(title: Text(fruit), value: fruit),
    ],
  );
}
