class PortalVisualizationExample extends StatefulWidget {
  @override
  State<PortalVisualizationExample> createState() => _VisualizerState();
}

class _VisualizerState extends State<PortalVisualizationExample> {
  final Value value = Value();

  @override
  Widget build(BuildContext context) => Padding(
    padding: const .all(8.0),
    child: Row(
      spacing: 4,
      children: [
        ListenableBuilder(
          listenable: value,
          builder: (context, _) {
            final child = (
              offset: value.childOffset,
              size: value.childSize,
              anchor: value.childAnchor,
            );
            final spacingOffset = FPortalSpacing(
              value.spacing,
              diagonal: value.diagonal,
            ).call(value.childAnchor, value.portalAnchor);
            final portal = (
              offset: spacingOffset,
              size: value.portalSize,
              anchor: value.portalAnchor,
            );
            final offset = value.overflow(
              Visualization.viewport,
              child,
              portal,
            );

            return Visualization(
              childOffset: value.childOffset,
              portalOffset: value.childOffset + offset,
              childSize: value.childSize,
              portalSize: value.portalSize,
              childAnchor: value.childAnchor,
              portalAnchor: value.portalAnchor,
            );
          },
        ),
        Expanded(child: Settings(value: value)),
      ],
    ),
  );
}
