class NoCascadingResizableExample extends StatefulWidget {
  @override
  State<NoCascadingResizableExample> createState() =>
      _NoCascadingResizableExampleState();
}

class _NoCascadingResizableExampleState
    extends State<NoCascadingResizableExample> {
  final _controller = FResizableController();

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) => DecoratedBox(
    decoration: BoxDecoration(
      border: .all(color: context.theme.colors.border),
      borderRadius: .circular(8),
    ),
    child: FResizable(
      control: .managed(controller: _controller),
      axis: .vertical,
      crossAxisExtent: 300,
      children: [
        .region(
          initialExtent: 200,
          minExtent: 100,
          builder: (_, data, _) =>
              Label(data: data, icon: FIcons.sunrise, label: 'Morning'),
        ),
        .region(
          initialExtent: 200,
          minExtent: 100,
          builder: (_, data, _) =>
              Label(data: data, icon: FIcons.sun, label: 'Afternoon'),
        ),
        .region(
          initialExtent: 200,
          minExtent: 100,
          builder: (_, data, _) =>
              Label(data: data, icon: FIcons.sunset, label: 'Evening'),
        ),
      ],
    ),
  );
}
